graphql.query:
query {
  sWAPI_Characters (where: { films: { exists: true }}) {
    count,
    edges {
      node {
        birthYear
        createdAt
        eyeColor
        films {
          edges {
            node {
              id
            }
          }
        }
        gender
        hairColor
        height
        homeworld {
          id
        }
        id
        mass
        name
        skinColor
        species {
          edges {
            node {
              id
            }
          }
        }
        starships {
          edges {
            node {
              id
            }
          }
        }
        updatedAt
        vehicles {
          edges {
            node {
              id
            }
          }
        }
      }
    }
  }
}

query {
  sWAPI_Characters (first: 1, where: { birthYear: { exists: false }}, order: [mass_DESC]) {
    edges {
      node {
        homeworld {
          name
        }
      }
    }
  }
}
